import React from "react";
import { formatDistanceToNow, differenceInDays } from "date-fns";
import {tr} from "date-fns/locale"

const Task = ({ taskObj, onComplete }) => {

  const deadlineDate = new Date(taskObj.deadline);
  const distance = formatDistanceToNow(deadlineDate,{locale:tr,addSuffix:true});
  const accentClass = differenceInDays(deadlineDate,new Date())<=3 ? "bg-maUrgent" : "bg-maNormal"; // bg-[#444] renk kodu , bg-red-500


  return (
    <div className="task p-6 bg-white rounded-md mt-4 shadow-[0_4px_5px_0px_rgba(0,0,0,0.1)]">
      <h3 className=" text-lg text-[#c8781a]">{taskObj.title}</h3>
      <div className="deadline">son teslim: <span className={accentClass}>{distance}</span></div>
      <p className="pt-2 pb-3 text-gray-800 text-sm">{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>{p}</span>
        ))}
      </div>
      {onComplete && <button className="block px-3 py-2 ml-auto bg-[#fecc91] 
      shadow-[0_4px_5px_0px_rgba(0,0,0,0.05)] rounded-sm border-0 cursor-pointer" onClick={() => onComplete(taskObj.id)}>TamamlandÄ±</button>}
    </div>
  );
};

export default Task;
